
class Solution:
    def threeSum(self, nums):
        nums.sort()
        answer = []
        for i in range(len(nums)):
            now = nums[i]
            one_p = i+1
            two_p = len(nums) -1

            while one_p < two_p:
                temp = now + nums[one_p] + nums[two_p]

                if temp == 0:
                    answer.append([now, nums[one_p], nums[two_p]])
                    one_p += 1
                    two_p -= 1

                elif temp > 0:
                    two_p -= 1
                else:
                    one_p += 1

        #  tuple 의 중요성!!
        #  tuple은 dic 에 등록 가능.
        unique_lists = []
        seen = set()

        for sublist in answer:
            t = tuple(sublist)
            if t not in seen:
                unique_lists.append(sublist)
                seen.add(t)
        return unique_lists


